// Deklaracja modułu TypeScript dla ikon Heroicons w wersji solid
// Informuje kompilator TypeScript o istnieniu modułu zewnętrznego
declare module '@heroicons/react/solid';

// Deklaracja modułu TypeScript dla ikon Heroicons w wersji outline
// Używane dla ikon z konturem (bez wypełnienia)
declare module '@heroicons/react/outline';
